import React from "react";
import ReactDOM from "react-dom";
import "./Square.css";
import "./Board.css";

class Square extends React.Component {
  constructor(props) {
    super(props);
    this.state = { clicked: false };
  }
  render() {
    return (
      <div className="square" onClick={this.props.onClick}>
        {this.props.char}
      </div>
    );
  }
  handleClick() {
    this.props.fn(this.props.num);
    if (!this.state.clicked) {
      this.setState({ clicked: true, message: "X" });
    }
  }
}
export class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      game_board: [null, null, null, null, null, null, null, null, null],
      x_turn: true,
    };
  }
  checkWin(squareNum) {
    let ch = this.state.game_board[squareNum];
    let checkDiagonals = squareNum % 2 == 0 ? true : false;
    // check row of particular square
    let squareRow = Math.floor(squareNum / 3);
    let possible_row_vals = [
      squareNum - 2,
      squareNum - 1,
      squareNum,
      squareNum + 1,
      squareNum + 2,
    ];
    let row_vals = [];
    for (let i = 0; i < possible_row_vals.length; i++) {
      if (Math.floor(possible_row_vals[i] / 3) == squareRow) {
        row_vals.push(possible_row_vals[i]);
      }
    }
    let row_win = true;
    for (let i = 0; i < row_vals.length; i++) {
      if (this.state.game_board[row_vals[i]] != ch) {
        row_win = false;
      }
    }
    if (row_win) console.log("Winner is " + ch);
    // check column of particular square
  }
  updateBoard(squareNum) {
    if (this.state.game_board[squareNum]) {
      return;
    }
    let game_board = this.state.game_board.slice();
    if (this.state.x_turn) {
      game_board[squareNum] = "X";
    } else {
      game_board[squareNum] = "O";
    }
    this.setState({ game_board: game_board, x_turn: !this.state.x_turn });
    this.checkWin(squareNum);
  }
  render() {
    return (
      <div className="bigboard">
        <div className="row">
          <Square
            num={0}
            char={this.state.game_board[0]}
            onClick={() => this.updateBoard(0)}
          />
          <Square
            num={1}
            char={this.state.game_board[1]}
            onClick={() => this.updateBoard(1)}
          />
          <Square
            num={2}
            char={this.state.game_board[2]}
            onClick={() => this.updateBoard(2)}
          />
        </div>
        <div className="row">
          <Square
            num={3}
            char={this.state.game_board[3]}
            onClick={() => this.updateBoard(3)}
          />
          <Square
            num={4}
            char={this.state.game_board[4]}
            onClick={() => this.updateBoard(4)}
          />
          <Square
            num={5}
            char={this.state.game_board[5]}
            onClick={() => this.updateBoard(5)}
          />
        </div>
        <div className="row">
          <Square
            num={6}
            char={this.state.game_board[6]}
            onClick={() => this.updateBoard(6)}
          />
          <Square
            num={7}
            char={this.state.game_board[7]}
            onClick={() => this.updateBoard(7)}
          />
          <Square
            num={8}
            char={this.state.game_board[8]}
            onClick={() => this.updateBoard(8)}
          />
        </div>
      </div>
    );
  }
}
