import React from "react";
import ReactDOM from "react-dom";
import "./Square.css";
import "./Board.css";

class Square extends React.Component {
  constructor(props) {
    super(props);
    this.state = { clicked: false };
  }
  render() {
    return (
      <div className="square" onClick={this.props.onClick}>
        {this.state.message}
      </div>
    );
  }
  handleClick() {
    this.props.fn(this.props.num);
    if (!this.state.clicked) {
      this.setState({ clicked: true, message: "X" });
    }
  }
}
export class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      game_board: [null, null, null, null, null, null, null, null, null],
      x_turn: true,
    };
  }
  updateBoard(squareNum) {
    if (this.state.game_board[squareNum]) {
      return;
    }
    console.log("Update board");
    let game_board = this.state.game_board.slice();
    if (this.state.x_turn) {
      game_board[squareNum] = "X";
    } else {
      game_board[squareNum] = "O";
    }
    console.log(game_board);
    this.setState(
      { game_board: game_board, x_turn: !this.state.x_turn },
      () => {
        console.log(this.state);
      }
    );
  }
  render() {
    return (
      <div className="bigboard">
        <div className="row">
          <Square
            num={0}
            char={this.state.game_board[0]}
            onClick={() => this.updateBoard(0)}
          />
          <Square char={this.state.game_board[1]} />
          <Square char={this.state.game_board[2]} />
        </div>
        <div className="row">
          <Square char={this.state.game_board[3]} />
          <Square char={this.state.game_board[4]} />
          <Square char={this.state.game_board[5]} />
        </div>
        <div className="row">
          <Square char={this.state.game_board[6]} />
          <Square char={this.state.game_board[7]} />
          <Square char={this.state.game_board[8]} />
        </div>
      </div>
    );
  }
}
