import React from "react";
import ReactDOM from "react-dom";
import "./Square.css";
import "./Board.css";

class Square extends React.Component {
  constructor(props) {
    super(props);
    this.state = { clicked: false, message: props.char };
  }
  render() {
    return <div className="square">{this.state.message}</div>;
  }
  handleClick() {
    this.props.fn(this.props.num);
    if (!this.state.clicked) {
      this.setState({ clicked: true, message: "X" });
    }
  }
}
export class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      game_board: [null, null, null, null, null, null, null, null, null],
      x_turn: true,
    };
  }
  updateBoard(squareNum) {
    console.log("Update board");
    console.log(this.state);
    let temp = this.state.game_board.slice();
    if (this.state.x_turn) {
      temp[squareNum] = "X";
    } else {
      temp[squareNum] = "O";
    }
    this.setState({ game_board: temp, x_turn: !this.state.x_turn });
  }
  render() {
    return (
      <div className="bigboard">
        <div className="row">
          <Square
            num={0}
            char={this.state.game_board[0]}
            onClick={() => this.updateBoard(0)}
          />
          <Square char={this.state.game_board[1]} />
          <Square char={this.state.game_board[2]} />
        </div>
        <div className="row">
          <Square char={this.state.game_board[3]} />
          <Square char={this.state.game_board[4]} />
          <Square char={this.state.game_board[5]} />
        </div>
        <div className="row">
          <Square char={this.state.game_board[6]} />
          <Square char={this.state.game_board[7]} />
          <Square char={this.state.game_board[8]} />
        </div>
      </div>
    );
  }
}
